import React, { Fragment, useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import RegisterHeader from './RegisterHeader';
import IndexFooter from './IndexFooter';
import RegisterValidation from './RegisterValidation';
import axios from 'axios';
import emailjs from '@emailjs/browser';


function Register() {

    //track changes in variables
    const [values, setValues] = useState({
        type: '',
        name: '',
        email: '',
        phoneNumber: '',
        password: '',
    })

    const handleInput = (event) => {
        setValues({ ...values, [event.target.name]: event.target.value })
    }
    const addField = (fieldName, fieldValue) => {
        setValues(prevValues => ({
            ...prevValues,
            [fieldName]: fieldValue
        }));
    };

    //track error 
    const [errors, setErrors] = useState({})
    
    useEffect(() => {
        setErrors(RegisterValidation(values))
    }, [values])

    //navigate between pages
    let history = useNavigate();

    //Send email
    const form = useRef();

    const sendEmail = (event) => {
        event.preventDefault();
        setErrors(RegisterValidation(values));
        console.log(values.resume);
        // Call the API to register the candidate
        axios.post('http://localhost/candidate_register.php', values)
            .then((response) => {
                if (response.data.status === 'success') {
                    // Registration successful
                    history('/login');
                } else {
                    // Registration failed, show an alert with the error message
                    alert(response.data.message);
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <Fragment>
            <header>
                <div className="header-container">
                    <h1>Register</h1>
                    <RegisterHeader />
                    <img src="assets/images/uta_logo.png" className="user-pic" alt=""></img>
                </div>
            </header>


            <main>
                {/* <!-- Candidate Registration Content --> */}
                <section id="registerpage">
                    <div className="overlay">
                        <div className="content">
                            <div id="candidateForm">
                                <form ref={form} action="" onSubmit={sendEmail}>

                                    <label htmlFor="type">Type:</label>
                                    <select id="type" name="type" onChange={handleInput} required value={values.type}>
                                        <option value="">Select registration type</option>
                                        <option value="candidate">Candidate</option>
                                        <option value="institute">Institute</option>
                                        <option value="officer">Officer</option>
                                        <option value="admin">Admin</option>
                                    </select>
                                    {errors.type && <span className='text-danger'>{errors.type}</span>}

                                    <br />

                                    <label htmlFor="name">Name:</label>
                                    <input
                                        type="text"
                                        id="name"
                                        name="name"
                                        placeholder='Enter name'
                                        onChange={handleInput}
                                        value={values.name}
                                        required
                                    />
                                    {errors.name && <span className='text-danger'>{errors.name}</span>}

                                    <label htmlFor="email">Email:</label>
                                    <input
                                        type="email"
                                        id="email"
                                        name="email"
                                        placeholder='Enter email'
                                        onChange={handleInput}
                                        value={values.email}
                                        required
                                    />
                                    {errors.email && <span className='text-danger'>{errors.email}</span>}

                                    <label htmlFor="phoneNumber">Phone Number:</label>
                                    <input
                                        type="text"
                                        id="phoneNumber"
                                        name="phoneNumber"
                                        placeholder='Enter phone number'
                                        onChange={handleInput}
                                        value={values.phoneNumber}
                                        required
                                    />
                                    {errors.phoneNumber && <span className='text-danger'>{errors.phoneNumber}</span>}

                                    <label htmlFor="password">Password:</label>
                                    <input
                                        type="password"
                                        id="password"
                                        name="password"
                                        placeholder='Enter password'
                                        onChange={handleInput}
                                        value={values.password}
                                        required
                                    />
                                    {errors.password && <span className='text-danger'>{errors.password}</span>}

                                    {/* New field */}
                                    {values.type === "candidate" && (
                                        <Fragment>
                                            {/* Check if joiningYear field exists, if not add it */}
                                            {values.address === undefined && (
                                                addField('address', '')
                                            )}
                                            {/* Render the label and input fields if joiningYear exists */}
                                            {values.address !== undefined && (
                                                <Fragment>
                                                    <label htmlFor="address">Address:</label>
                                                    <input
                                                        type="text"
                                                        id="address"
                                                        name="address"
                                                        placeholder='Enter address'
                                                        onChange={handleInput}
                                                        value={values.address}
                                                        required
                                                    />
                                                </Fragment>
