import React, { Fragment, useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom'; // Import useParams to access URL parameters
import InstituteHeader from './InstituteHeader';
import IndexFooter from './IndexFooter';
import axios from 'axios';

function JobDetails() {
    // State to store job details and loading state
    const [jobDetails, setJobDetails] = useState([]);
    const [loading, setLoading] = useState(true); // Initialize loading state as true
    
    // Get the job ID from URL parameters
    const { jobId } = useParams();
    
    // Fetch job details from the API
    useEffect(() => {
        axios.post('http://localhost/job_fetch_by_Id.php', { id: jobId })
            .then((response) => {
                if (response.data.status === 'success') {
                    setJobDetails(response.data.details);
                } else {
                    console.error(response.data.message);
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            })
            .finally(() => {
                setLoading(false); // Set loading state to false after data is fetched
            });
    }, [jobId]);
    console.log(jobDetails);
    // Render loading message while fetching job details
    if (loading) {
        return <p>Loading...</p>;
    }

    // Render job details
    return (
        <Fragment>
            <header>
                <div className="header-container">
                    <h1>Details</h1>
                    <InstituteHeader />
                    <img src="assets/images/uta_logo.png" className="user-pic" alt=""></img>
                </div>
            </header>
            <main>
                <div>
                    {jobDetails.map((job) => (
                        <div key={job.positionID}>
                            <h1>{jobDetails.title}</h1>
                            <p>Description: {jobDetails.description}</p>
                            <p>Field: {jobDetails.fieldOfStudy}</p>
                            <p>Location: {jobDetails.location}</p>
                            <p>Salary: {jobDetails.salary}</p>
                        </div>
                    ))}
                </div>
            </main>
            
            <IndexFooter />
        </Fragment>
    );
}

export default JobDetails;
