import React, { Fragment, useState } from 'react';
import { Link } from 'react-router-dom';
import CandidateHeader from './CandidateHeader';
import IndexFooter from './IndexFooter';
import axios from 'axios';


function CandidateJobApplied() {

    const userID = window.localStorage.getItem("userId");


    // State to store the job data received from the API
    const [jobs, setJobs] = useState([]);

    const handleApplication = (event) => {
        event.preventDefault();

        const data = {
            userId: userID,
        };
        console.log(data)

        // Call the API to fetch the job data
        axios.post('http://localhost/application_fetch.php', data)
            .then((response) => {
                if (response.data.status === 'success') {
                    // Job data fetched successfully, update the jobs state
                    setJobs(response.data.jobs);
                } else {
                    // Handle the case where no job data is found or there's an error
                    console.error(response.data.message);
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }
    useEffect(() => {
        const jobIDs = jobs.map(job => job.positionID);
        jobIDs.forEach(jobID => {
            axios.post('http://localhost/job_fetch.php', { jobID })
                .then(response => {
                    if (response.data) {
                        setJobs(prevJobs => {
                            return prevJobs.map(job => {
                                if (job.positionID === jobID) {
                                    return { ...job, title: response.data.title, description: response.data.description };
                                }
                                return job;
                            });
                        });
                    }
                })
                .catch(error => {
                    console.error('Error:', error);
                });
        });
    }, [jobs]);
    console.log(jobs);

    return (
        <Fragment>

            <header>
                <div className="header-container">
                    <h1>Applied Jobs</h1>
                    <CandidateHeader />
                    <img src="assets/images/uta_logo.png" className="user-pic" alt=""></img>
                </div>
            </header>

            <main>
                {/* <!-- Jobs Applied Content --> */}
                <section id="jobsearch" className="tile">
                    <div className="search">
                        <input type="submit" value="Fetch Applications" className="cta-button" onClick={handleApplication} />
                    </div>

                    {/* <!-- Job Applied Table --> */}
                    <div className="job-applied">
                        <table>
                            <thead>
                                <tr>
                                    <th>Job Title</th>
                                    <th>Description</th>
                                    <th>Status</th>
                                    <th>Date_Applied</th>
                                </tr>
                            </thead>
                            <tbody>
                                {jobs.map((job) => (
                                    <tr key={job.positionID}>
                                        <td>{job.title}</td>
                                        <td>{job.description}</td>
                                        <td>{job.status}</td>
                                        <td>{job.dateApplied}</td>
                                    </tr>
                                ))}
                            </tbody>

                        </table>
                    </div>
                </section>
            </main>

            <IndexFooter />
        </Fragment >
    )
}

export default CandidateJobApplied;
