import React, { Fragment, useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom'; // Import useParams to access URL parameters
import InstituteHeader from './InstituteHeader';
import IndexFooter from './IndexFooter';
import axios from 'axios';

function JobDetails() {
    // State to store job details
    const [jobDetails, setJobDetails] = useState(null);
    
    // Get the job ID from URL parameters
    const { jobId } = useParams();
    // Fetch job details from the API
    useEffect(() => {
        const fetchJobDetails = async () => {
            try {
                const response = await axios.post(`http://localhost/job_fetch_by_Id.php`,{ id: jobId}); // Replace 'http://localhost/jobs/${jobId}' with your API endpoint
                setJobDetails(response.data.details);
            } catch (error) {
                console.error('Error fetching job details:', error);
            }
        };

        fetchJobDetails();
    }, [jobId]);
    console.log(jobDetails);
    // Render loading message while fetching job details
    if (!jobDetails) {
        return <p>Loading...</p>;
    }

    // Render job details
    return (
        <Fragment>
            <header>
                <div className="header-container">
                    <h1>Details</h1>
                    <InstituteHeader />
                    <img src="assets/images/uta_logo.png" className="user-pic" alt=""></img>
                </div>
            </header>
            <main>
                <div>
                    <h1>{jobDetails.title}</h1>
                    <p>Description: {jobDetails.description}</p>
                    <p>Field: {jobDetails.fieldOfStudy}</p>
                    <p>Location: {jobDetails.location}</p>
                    <p>Salary: {jobDetails.salary}</p>
                </div>
            </main>
            
            <IndexFooter />
        </Fragment>
    );
}

export default JobDetails;
