import React, { Fragment, useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import IndexHeader from './IndexHeader';
import IndexFooter from './IndexFooter';
import axios from 'axios';

function Contact() {
    const [values, setValues] = useState({
        name: '',
        email: '',
        comments: '',
    })
    const handleInput = (event) => {
        setValues({ ...values, [event.target.name]: event.target.value })
    }
    let history = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();

        const data = {
            name: values.name,
            email: values.email,
            comments: values.comments,
        };

        console.log(data);

        // registerRequest();

        // Call the API to register the candidate
        axios.post('http://localhost/contact.php', data)
            .then((response) => {
                if (response.data.status === 'success') {
                    // Registration successful, navigate to the next page with URL parameter
                    alert('Feedback Sent')
                    history.push('/candidate_dashboard')
                    // history.push('/institute_jobposted?jobs=' + JSON.stringify(response.data.jobs));
                } else {
                    // Registration failed, show an alert with the error message
                    alert(response.data.message);
                    history.push('/contact');
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };
    return (
        <Fragment>

            <main>
                {/* <!-- Contact Content --> */}
                <section id="contact" className="contact-section">
                    <h2>Contact ADMIN</h2>
                    <div className="row">
                        <div className="col-8 col-12-small form-section">
                            <form method="post" action="contact_admin.php">
                                <div className="row gtr-uniform gtr-50">
                                    <input type="text" name="name" id="name" placeholder="Name" />
                                    <input type="email" name="email" id="email" placeholder="Email" />
                                    <textarea name="comments" id="message" placeholder="Message" rows="4"></textarea>
                                </div>
                                <input type="submit" value="Send Message" className="button primary" />
                            </form>
                        </div>
                        <div className="col-4 col-12-small address-section">
                            <div className="address">
                                <h3 className="icon solid fa-home"><span className="label">Address</span></h3>
                                <p>
                                    Street Address<br />
                                    City, State Pincode<br />
                                    Country
                                </p>
                            </div>
                            <div className="contact-info">
                                <h3 className="icon solid fa-mobile-alt"><span className="label">Phone</span></h3>
                                <p>(XXX) XXX-XXXX</p>
                                <h3 className="icon solid fa-envelope"><span className="label">Email</span></h3>
                                <p><a href="mailto:jayasurya.guna@gmail.com">admin@gmail.com</a></p>
                            </div>
                        </div>
                    </div>
                </section>
            </main>
        </Fragment >
    )
}

export default Contact;
